import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Typeahead } from "react-bootstrap-typeahead";
import useSwr from "swr";

import "react-bootstrap-typeahead/css/Typeahead.css";

// import * as data from ".././data/db-stops.json";

const fetcher = (...args) => fetch(...args).then((response) => response.json());
// const uncleanData = data.results;

// const nameOfStops = uncleanData.map((stop) => ({
//   label: stop.fullname,
//   id: parseInt(stop.stopid),
// }));

export default function Location() {
  const url =
    "https://data.smartdublin.ie/cgi-bin/rtpi/busstopinformation?operator=bac";
  const { data, error } = useSwr(url, { fetcher });
  // Creating a placeholder JSON object while the real one is fetched.
  const stopData =
    data && !error
      ? data
      : {
          results: [
            {
              stopid: "0",
              fullname: "Test0",
              latitude: "53.60395194",
              longitude: "-6.184445556",
              operators: [
                {
                  routes: ["46A"],
                },
              ],
            },
            {
              stopid: "1",
              fullname: "Test1",
              latitude: "53.0706738889",
              longitude: "-6.063445",
              operators: [
                {
                  routes: ["46A"],
                },
              ],
            },
            {
              stopid: "2",
              fullname: "Test2",
              latitude: "53.13340306",
              longitude: "-6.614838889",
              operators: [
                {
                  routes: ["46A"],
                },
              ],
            },
          ],
        };

  console.log("Stop data here", stopData.results);
  const nameOfStops = stopData.results.map((stop) => ({
    label: stop.fullname,
    id: parseInt(stop.stopid),
  }));

  console.log(nameOfStops);
  // class LocationForms extends Component {
  //   state = {
  //     selected: [],
  //   };

  // render() {
  return (
    <Form>
      <Form.Group controlId="formDeparture">
        <Form.Label>Departure</Form.Label>
        <Typeahead
          {...this.state}
          id="basic-example"
          onChange={(selected) => this.setState({ selected })}
          options={nameOfStops}
          placeholder="Choose a departure stop..."
        />
      </Form.Group>

      <Form.Group controlId="formArrival">
        <Form.Label>Arrival</Form.Label>
        <Typeahead
          {...this.state}
          id="basic-example"
          onChange={(selected) => this.setState({ selected })}
          options={nameOfStops}
          placeholder="Choose an arrival stop..."
        />
      </Form.Group>
      <Form.Group controlId="formModeSubmit">
        <Form.Check
          style={{ paddingBottom: "10px" }}
          type="switch"
          id="custom-switch"
          label="Tourist Mode"
        />
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form.Group>
    </Form>
  );
  // }
}

// export default LocationForms;
