import React, { useState } from "react";
import {
  useLoadScript,
  GoogleMap,
  Marker,
  InfoWindow,
  MarkerClusterer,
} from "@react-google-maps/api";

import googleMapApiKey from ".././config";
import * as data from ".././data/db-stops.json";

// Import custom styles to customize the style of Google Map
const styles = require("../data/GoogleMapStyles.json");

const options = {
  imagePath:
    "https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m",
};

const mapOptions = {
  styles: styles,
  disableDefaultUI: true,
  zoomControl: true,
};

const rawData = data.results;
const myStops = rawData.map((stop) => ({
  type: "Feature",
  properties: {
    id: stop.stopid,
    fullname: stop.fullname,
    routes: stop.operators[0].routes,
  },
  geometry: {
    type: "Point",
    pos: {
      lat: parseFloat(stop.latitude),
      lng: parseFloat(stop.longitude),
    },
  },
}));

export default function T13GoogleMap() {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: "AIzaSyDmDEDnZrcDTrRnaCt-fDtCM-xRnML5jyM",
  });
  // The things we need to track in state
  // const [mapRef, setMapRef] = useState(null);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [markerMap, setMarkerMap] = useState({});
  // consts: [53.349804, -6.260310] - Dublin
  const [center, setCenter] = useState({ lat: 53.349804, lng: -6.26031 });
  const [zoom, setZoom] = useState(10);
  const [infoOpen, setInfoOpen] = useState(false);

  // // Iterate myStops to size, center, and zoom map to contain all markers
  // const fitBounds = (map) => {
  //   const bounds = new window.google.maps.LatLngBounds();
  //   myStops.map((stop) => {
  //     bounds.extend(stop.geometry.pos);
  //     return stop.properties.id;
  //   });
  //   map.fitBounds(bounds);
  // };

  // const loadHandler = (map) => {
  //   // Store a reference to the google map instance in state
  //   setMapRef(map);
  //   // Fit map bounds to contain all markers
  //   fitBounds(map);
  // };

  const mapRefAlt = React.useRef();
  const onMapLoad = React.useCallback((clom) => {
    mapRefAlt.current = clom;
  }, []);

  const panTo = React.useCallback(({ lat, lng }) => {
    mapRefAlt.current.panTo({ lat, lng });
    mapRefAlt.current.setZoom(16);
  }, []);

  // We have to create a mapping of our places to actual Marker objects
  const markerLoadHandler = (marker, stop) => {
    return setMarkerMap((prevState) => {
      return { ...prevState, [stop.properties.id]: marker };
    });
  };

  const markerClickHandler = (event, place) => {
    // Remember which stop was clicked
    setSelectedPlace(place);

    // Required so clicking a 2nd marker works as expected
    if (infoOpen) {
      setInfoOpen(false);
    }

    setInfoOpen(true);

    // // If you want to zoom in a little on marker click
    // if (zoom < 11) {
    //   setZoom(11);
    // }

    // if you want to center the selected Marker
    //setCenter(place.pos)
  };

  if (loadError) return "Error";
  if (!isLoaded) return "Loading...";

  return (
    <div>
      <GoogleMap
        onLoad={onMapLoad}
        // onLoad={loadHandler}
        // onCenterChanged={() => setCenter(mapRef.getCenter().toJSON())}
        center={center}
        zoom={zoom}
        maxZoom={13}
        options={mapOptions}
        mapContainerStyle={{
          height: "94vh",
        }}
      >
        <Locate panTo={panTo} />
        <MarkerClusterer options={options}>
          {(clusterer) =>
            myStops.map((stop) => (
              <Marker
                key={stop.properties.id}
                position={stop.geometry.pos}
                clusterer={clusterer}
                onLoad={(marker) => markerLoadHandler(marker, stop)}
                onClick={(event) => markerClickHandler(event, stop)}
              />
            ))
          }
        </MarkerClusterer>

        {infoOpen && selectedPlace && (
          <InfoWindow
            anchor={markerMap[selectedPlace.properties.id]}
            onCloseClick={() => setInfoOpen(false)}
          >
            <div>
              <h3>Stop Number: {selectedPlace.properties.id}</h3>
              <h5>Routes: {selectedPlace.properties.routes.join(", ")}</h5>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    </div>
  );
}

function Locate({ panTo }) {
  return (
    <button
      className="locate"
      onClick={() => {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            panTo({
              // lat: position.coords.latitude,
              // lng: position.coords.longitude,
              lat: 53.343,
              lng: -6.2562,
            });
          },
          () => null
        );
      }}
    >
      <img src="/compass.svg" alt="compass" />
    </button>
  );
}
