import React from "react";
import styled from "styled-components";
import Button from "react-bootstrap/Button";

import Container from "react-bootstrap/Container";
import T13GoogleMap from "./components/T13GoogleMap";
// import Api from "./components/Api";
import DateTimeSelector from "./components/DateTimeSelector";
import BrandBar from "./components/BrandBar";
import LocationForms from "./components/Location Forms";

import "bootstrap/dist/css/bootstrap.min.css";

const Wrapper = styled.main`
  width: 100%;
  height: 100%;
`;

// We’ll start in the App.js file. The file’s state looks like this:
state = {
  questions: [
    { id: "fdsd", title: "Why is the sky blue?" },
    { id: "adsf", title: "Who invented pizza?" },
    { id: "afdsf", title: "Is green tea overrated?" },
  ],
  displayQuestions: false,
};

// In order to conditionally render the Questions on click, we add an onClick method to
// App.js, which toggles between true and false for the state value of displayQuestions:

displayQuestion = () => {
  this.setState({
    displayQuestions: !this.state.displayQuestions,
  });
};

export default function App() {
  const renderPage = () => {
    return (
      <Container fluid>
        <BrandBar></BrandBar>
        <Wrapper style={{ width: "75%", float: "left" }}>
          <T13GoogleMap></T13GoogleMap>
        </Wrapper>

        <Wrapper style={{ width: "25%", float: "right" }}>
          <Container style={{ paddingTop: "15vh" }}>
            <DateTimeSelector></DateTimeSelector>
            <LocationForms></LocationForms>
            <Button
              variant="primary"
              type="submit"
              onClick={this.displayQuestion}
            >
              Submit
            </Button>
            {/* <Api></Api> */}
          </Container>
        </Wrapper>
      </Container>
    );
  };

  return renderPage();
}
