import React from "react";
import DatePicker from "react-datepicker";
import { Typeahead } from "react-bootstrap-typeahead";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import ModelApi from "./ModelApi";

import "react-datepicker/dist/react-datepicker.css";
import ".././styles.css";
import RouteStopsApi from "./RouteStopsApi";

const PredictionInput = (props) => {
  // Track user selected routes and directions.
  const [routeSelect, setRouteSelect] = React.useState("");
  const [directionSelect, setDirectionSelect] = React.useState("");
  // Tracking this in state in order to enable a submit button to trigger the ModelApi response.
  const [destinationSelected, setDestinationSelected] = React.useState([]);
  // Get an array of the stops associated with the users selected route
  const getStops = RouteStopsApi(props);
  // Conver that array if strings to integers.
  const a = getStops;
  // A map method for Arrays, applying a function to all elements of an array.
  const dir1Nums46a = a.map(function (x) {
    return parseInt(x, 10);
  });

  // console.log(dir1Nums46a);

  // const dir1Nums46a = require("../data/46A_dir1_stops.json");
  console.log("dir1Nums46a here", dir1Nums46a);
  const dir1Stops46a = [];

  for (var q = 0; q < props.parsedStops.length; q++) {
    for (var r = 0; r < dir1Nums46a.length; r++) {
      if (dir1Nums46a[r] === parseInt(props.parsedStops[q].id)) {
        dir1Stops46a.push(props.parsedStops[q].description);
      }
    }
  }

  return (
    <Form>
      <Form.Group controlId="formRoute">
        <Typeahead
          id="basic-example"
          options={["46A"]}
          maxVisible={2}
          placeholder="Select a route: e.g. 46A"
          onChange={(address) => {
            try {
              for (var i = 0; i < props.parsedStops.length; i++) {
                if (String(address) === props.parsedStops[i].description) {
                  const id = props.parsedStops[i].id;
                  const lat = props.parsedStops[i].geometry.lat;
                  const lng = props.parsedStops[i].geometry.lng;
                  props.originChoice({ lat, lng });
                  props.panTo({ lat, lng });
                  props.originNumberChoice({ id });
                }
              }
            } catch (error) {
              console.log("ðŸ˜± Error: ", error);
            }
          }}
        />
      </Form.Group>
      <Form.Group controlId="formRoute">
        <Typeahead
          id="basic-example"
          options={["Phoenix Park", "Dun Laoghaire"]}
          maxVisible={2}
          placeholder="Select a destination: e.g. Phoenix Park"
        />
      </Form.Group>

      <Form.Group controlId="formTimeOfTravel">
        <Form.Label>
          <strong>When are you travelling?</strong>
        </Form.Label>
        <DatePicker
          selected={props.selectedTime}
          onChange={(date) => props.timeChoice(date)}
          showTimeSelect
          timeFormat="HH:mm"
          timeIntervals={60}
          timeCaption="time"
          dateFormat="MMMM d, yyyy h:mm aa"
        />

        <Form.Group controlId="formDeparture" style={{ paddingTop: "1vh" }}>
          <Typeahead
            id="basic-example"
            options={dir1Stops46a}
            placeholder="Departing from: e.g. Stop 334, D'Olier Street"
            onChange={(address) => {
              try {
                for (var i = 0; i < props.parsedStops.length; i++) {
                  if (String(address) === props.parsedStops[i].description) {
                    const id = props.parsedStops[i].id;
                    const lat = props.parsedStops[i].geometry.lat;
                    const lng = props.parsedStops[i].geometry.lng;
                    props.originChoice({ lat, lng });
                    props.panTo({ lat, lng });
                    props.originNumberChoice({ id });
                  }
                }
              } catch (error) {
                console.log("ðŸ˜± Error: ", error);
              }
            }}
          />
        </Form.Group>

        <Form.Group controlId="formArrival">
          <Typeahead
            id="basic-example"
            options={dir1Stops46a}
            placeholder="Destination: e.g. Stop 2007, Stillorgan Road"
            onChange={(s) => {
              try {
                // console.log("button triggered");
                for (var i = 0; i < props.parsedStops.length; i++) {
                  if (String(s) === props.parsedStops[i].description) {
                    const lat = props.parsedStops[i].geometry.lat;
                    const lng = props.parsedStops[i].geometry.lng;
                    props.panTo({ lat, lng });
                    setDestinationSelected(s);
                  }
                }
              } catch (error) {
                console.log("ðŸ˜± Error: ", error);
              }
            }}
          />
          <Form.Group style={{ paddingTop: "1vh" }}>
            <Button
              style={{ width: "100%" }}
              onClick={() => {
                try {
                  // console.log("button triggered");
                  for (var i = 0; i < props.parsedStops.length; i++) {
                    if (
                      String(destinationSelected) ===
                      props.parsedStops[i].description
                    ) {
                      const id = props.parsedStops[i].id;
                      const lat = props.parsedStops[i].geometry.lat;
                      const lng = props.parsedStops[i].geometry.lng;
                      props.destinationChoice({ lat, lng });
                      props.destinationNumberChoice({ id });
                    }
                  }
                } catch (error) {
                  console.log("ðŸ˜± Error: ", error);
                }
              }}
            >
              Submit
            </Button>
          </Form.Group>
        </Form.Group>
      </Form.Group>
      <ModelApi
        timeDayMonth={props.timeDayMonth}
        originNumber={props.originNumber}
        destinationNumber={props.destinationNumber}
      ></ModelApi>
    </Form>
  );
};

export default PredictionInput;
