import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Typeahead } from "react-bootstrap-typeahead";
import ToggleButton from "react-bootstrap/ToggleButton";

import "react-bootstrap-typeahead/css/Typeahead.css";

import * as data from ".././data/db-stops.json";
const uncleanData = data.results;

const nameOfStops = uncleanData.map((stop) => ({
  label: stop.fullname,
  id: parseInt(stop.stopid),
}));

console.log(nameOfStops);

class LocationForms extends Component {
  state = {
    selected: [],
  };

  render() {
    return (
      <Form>
        <Form.Group controlId="formBasicDeparture">
          <Form.Label>Departure</Form.Label>
          <Typeahead
            {...this.state}
            id="basic-example"
            onChange={(selected) => this.setState({ selected })}
            options={nameOfStops}
            placeholder="Choose a departure stop..."
          />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Arrival</Form.Label>
          <Typeahead
            {...this.state}
            id="basic-example"
            onChange={(selected) => this.setState({ selected })}
            options={nameOfStops}
            placeholder="Choose an arrival stop..."
          />
        </Form.Group>
        <ButtonGroup toggle className="mb-2">
          <ToggleButton
            type="checkbox"
            variant="secondary"
            checked={checked}
            value="1"
            onChange={(e) => setChecked(e.currentTarget.checked)}
          >
            Checked
          </ToggleButton>
        </ButtonGroup>
        <br />
        <ButtonGroup toggle>
          {radios.map((radio, idx) => (
            <ToggleButton
              key={idx}
              type="radio"
              variant="secondary"
              name="radio"
              value={radio.value}
              checked={radioValue === radio.value}
              onChange={(e) => setRadioValue(e.currentTarget.value)}
            >
              {radio.name}
            </ToggleButton>
          ))}
        </ButtonGroup>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    );
  }
}

export default LocationForms;
