import React from "react";
import Table from "react-bootstrap/Table";

const axios = require("axios");

const Axios = (props) => {
  const placeholder = {
    results: [
      {
        arrivaldatetime: "08/07/2020 00:00:00",
        route: "",
        destination: "",
        duetime: "",
      },
    ],
  };
  const [rawStopData, setRawStopData] = React.useState(placeholder);

  React.useEffect(() => {
    fetch("/api/real_time_api", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });
  }, []);

  //   axios.post("/api/real_time_api", { props });

  console.log("before assign", rawStopData);
  console.log("Hi props", props);
  React.useEffect(() => {
    axios.get(`/api/real_time_api`).then((res) => {
      const rawStopData = res.data;
      console.log("after assign", rawStopData);
      setRawStopData(rawStopData);
    });
  }, []);

  const stopData = rawStopData.results;
  const realInfo = stopData.map((info) => ({
    id: info.arrivaldatetime,
    route: info.route,
    destination: info.destination,
    arrivaltime: info.duetime,
  }));
  if (realInfo === []) {
    const realInfo = placeholder;
  }
  console.log("usable data", realInfo);

  return (
    <div style={{ maxHeight: "15vh", overflowY: "scroll" }}>
      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>Route</th>
            <th>Destination</th>
            <th>Arrival (mins)</th>
          </tr>
        </thead>
        <tbody>
          {realInfo.map((info) => {
            return (
              <tr key={info.id}>
                <td>{info.route}</td>
                <td>{info.destination}</td>
                <td>{info.arrivaltime}</td>
              </tr>
            );
          })}
        </tbody>
      </Table>
    </div>
  );
};

export default Axios;
