import React, { useState } from "react";

const axios = require("axios");

export default function RouteStopsApi(routeSelect, directionSelect) {
  const [routeStopsResponse, setRouteStopsResponse] = useState({
    route_stops_response: [],
  });
  console.log("route select made it in!", routeSelect[0]);

  React.useEffect(
    () => {
      if (routeSelect[0] === undefined || directionSelect === undefined) {
        console.log("this got rendered");
        // initial render should be nothing.
        return undefined;
      } else console.log("I got RENDERED!!!");
      axios
        .get(`/api/route_stops`, {
          params: {
            chosenRoute: routeSelect[0],
            chosenDirection: directionSelect,
          },
        })
        .then((res) => {
          console.log("route select made it THIS FAR!", routeSelect[0]);
          const routeStopsResponse = res.data;
          setRouteStopsResponse(routeStopsResponse);

          console.log(
            "setModelResponse has been triggered with the following values: "
          );
        });
    },
    // Listening for changes to props in order to
    // trigger a call to the API  to re-render the component.
    [routeSelect[0], directionSelect]
  );

  return routeStopsResponse.route_stops_response;
}
