import React from "react";
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng,
} from "react-places-autocomplete";
const { Component } = require("react");
const {
  DirectionsRenderer,
  DirectionsService,
  Autocomplete,
} = require("@react-google-maps/api");

class Directions extends Component {
  constructor(props) {
    super(props);

    this.state = {
      response: null,
      travelMode: "TRANSIT",
      origin: "",
      destination: "",
      originAddress: "",
      destinationAddress: "",
    };
    //Autocomplete stuff, won't keep
    this.autocomplete = null;

    this.onLoad = this.onLoad.bind(this);
    this.onPlaceChanged = this.onPlaceChanged.bind(this);
    //Autocomplete stuff, won't keep.

    this.directionsCallback = this.directionsCallback.bind(this);
    this.getOrigin = this.getOrigin.bind(this);
    this.getDestination = this.getDestination.bind(this);
    this.onClick = this.onClick.bind(this);
    this.onMapClick = this.onMapClick.bind(this);
  }

  directionsCallback(response) {
    console.log("hi, ", response);

    if (response !== null) {
      if (response.status === "OK") {
        this.setState(() => ({
          response,
        }));
      } else {
        console.log("response: ", response);
      }
    }
  }

  getOrigin(ref) {
    this.origin = ref;
  }

  getDestination(ref) {
    this.destination = ref;
  }

  onClick() {
    if (this.origin.value !== "" && this.destination.value !== "") {
      this.setState(() => ({
        origin: this.origin.value,
        destination: this.destination.value,
      }));
    }
  }

  onMapClick(...args) {
    console.log("onClick args: ", args);
  }

  handleChangeOrigin = (originAddress) => {
    this.setState({ originAddress });
  };

  handleSelectOrigin = (originAddress) => {
    geocodeByAddress(originAddress)
      .then((results) => getLatLng(results[0]))
      .then((latLng) => console.log("Success", latLng))
      .catch((error) => console.error("Error", error));
    this.setState({ originAddress });
  };

  handleChangeDestination = (destinationAddress) => {
    this.setState({ destinationAddress });
  };

  handleSelectDestination = (destinationAddress) => {
    geocodeByAddress(destinationAddress)
      .then((results) => getLatLng(results[0]))
      .then((latLng) => console.log("Success", latLng))
      .catch((error) => console.error("Error", error));
    this.setState({ destinationAddress });
  };
  //Autocomplete stuff, won't keep.
  onLoad(autocomplete) {
    console.log("autocomplete: ", autocomplete);

    this.autocomplete = autocomplete;
  }

  onPlaceChanged() {
    if (this.autocomplete !== null) {
      console.log(this.autocomplete.getPlace());
    } else {
      console.log("Autocomplete is not loaded yet!");
    }
  }
  // Autocomplete stuff, won't keep.

  render() {
    const defaultStyles = {
      root: {
        position: "relative",
        paddingBottom: "0px",
      },
      input: {
        display: "inline-block",
        width: "100%",
        padding: "10px",
      },
      autocompleteContainer: {
        position: "absolute",
        top: "100%",
        backgroundColor: "white",
        border: "1px solid #555555",
        width: "100%",
      },
      autocompleteItem: {
        backgroundColor: "#ffffff",
        padding: "10px",
        color: "#555555",
        cursor: "pointer",
      },
      autocompleteItemActive: {
        backgroundColor: "#fafafa",
      },
    };
    return (
      <div className="">
        <div className="boxes">
          <div className="row">
            <div className="col-md-6 col-lg-4">
              <PlacesAutocomplete
                value={this.state.originAddress}
                onChange={this.handleChangeOrigin}
                onSelect={this.handleSelectOrigin}
                searchOptions={{
                  location: new window.google.maps.LatLng(53, -6),
                  radius: 1000 * 35,
                  types: ["address"],
                }}
                styles={defaultStyles}
              >
                {({
                  getInputProps,
                  suggestions,
                  getSuggestionItemProps,
                  loading,
                }) => (
                  <div>
                    <input
                      ref={this.getOrigin}
                      {...getInputProps({
                        placeholder: "   Origin",
                        className: "location-search-input",
                      })}
                    />
                    <div className="autocomplete-dropdown-container">
                      {loading && <div>Loading...</div>}
                      {suggestions.map((suggestion) => {
                        // inline style for demonstration purpose
                        const style = suggestion.active
                          ? { backgroundColor: "#42a5f5", cursor: "pointer" }
                          : { backgroundColor: "#ffffff", cursor: "pointer" };
                        return (
                          <div
                            className="input-suggestion"
                            {...getSuggestionItemProps(suggestion, {
                              style,
                            })}
                          >
                            <span>{suggestion.description}</span>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                )}
              </PlacesAutocomplete>
            </div>
            <div className="col-md-6 col-lg-4">
              <PlacesAutocomplete
                value={this.state.destinationAddress}
                onChange={this.handleChangeDestination}
                onSelect={this.handleSelectDestination}
                searchOptions={{
                  location: new window.google.maps.LatLng(53, -6),
                  radius: 1000 * 35,
                  types: ["address"],
                }}
              >
                {({
                  getInputProps,
                  suggestions,
                  getSuggestionItemProps,
                  loading,
                }) => (
                  <div>
                    <input
                      ref={this.getDestination}
                      {...getInputProps({
                        placeholder: "   Destination",
                        className: "location-search-input",
                      })}
                      onSelectCapture={this.onClick}
                    />
                    <div className="autocomplete-dropdown-container">
                      {loading && <div>Loading...</div>}
                      {suggestions.map((suggestion) => {
                        // inline style for demonstration purpose
                        const style = suggestion.active
                          ? { backgroundColor: "#42a5f5", cursor: "pointer" }
                          : { backgroundColor: "#ffffff", cursor: "pointer" };
                        return (
                          <div
                            className="input-suggestion"
                            {...getSuggestionItemProps(suggestion, {
                              style,
                            })}
                          >
                            <span>{suggestion.description}</span>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                )}
              </PlacesAutocomplete>
            </div>
            <div className="col-md-6 col-lg-4">
              <Autocomplete
                onLoad={this.onLoad}
                onPlaceChanged={this.onPlaceChanged}
              >
                <input
                  type="text"
                  placeholder="Customized your placeholder"
                  style={{
                    boxSizing: `border-box`,
                    border: `1px solid transparent`,
                    width: `240px`,
                    height: `32px`,
                    padding: `0 12px`,
                    borderRadius: `3px`,
                    boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
                    fontSize: `14px`,
                    outline: `none`,
                    textOverflow: `ellipses`,
                    position: "absolute",
                    left: "50%",
                    marginLeft: "-120px",
                  }}
                />
              </Autocomplete>
            </div>
          </div>
        </div>

        <div className="map-container">
          {this.state.destination !== "" && this.state.origin !== "" && (
            <DirectionsService
              // required
              options={{
                destination: this.state.destination,
                origin: this.state.origin,
                travelMode: this.state.travelMode,
              }}
              // required
              callback={this.directionsCallback}
            />
          )}

          {this.state.response !== null && (
            <DirectionsRenderer
              // required
              options={{
                directions: this.state.response,
              }}
              panel={document.getElementById("panel")}
            />
          )}
        </div>
      </div>
    );
  }
}

export default Directions;
