const Axios = () => {
  const [rawStopData, setRawStopData] = useState({
    results: [
      {
        arrivaldatetime: "08/07/2020 00:00:00",
        route: "",
        destination: "",
        duetime: "",
      },
    ],
  });

  console.log("before assign", rawStopData);

  React.useEffect(() => {
    axios.get(`/api/real_time_api`).then((res) => {
      const rawStopData = res.data;
      console.log("after assign", rawStopData);
      setRawStopData(rawStopData);
    });
  });

  const stopData = rawStopData.results;
  const realInfo = stopData.map((info) => ({
    id: info.arrivaldatetime,
    route: info.route,
    destination: info.destination,
    arrivaltime: info.duetime,
  }));
  console.log("usable data", realInfo);

  return (
    <div>
      <p>Information from rtpi:</p>
      <p></p>
    </div>
  );
};

export default Axios;
