import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import { Typeahead } from "react-bootstrap-typeahead";
import "react-bootstrap-typeahead/css/Typeahead.css";

import * as data from ".././data/db-stops.json";
const rawData = data.results;

const nameOfStops = rawData.map((stop) => ({
  label: "Stop " + stop.stopid + ", " + stop.fullname,
  id: parseInt(stop.stopid),
}));

class LocationForms extends Component {
  state = {
    selected: [],
  };

  componentDidMount() {}

  render() {
    return (
      <Form>
        <Form.Group controlId="formDeparture">
          <Form.Label>Departure</Form.Label>
          <Typeahead
            {...this.state}
            id="basic-example"
            onChange={(selected) => this.setState({ selected })}
            console={console.log((selected) => this.setState({ selected }))}
            options={nameOfStops}
            placeholder="Choose a departure stop..."
          />
        </Form.Group>
        <Form.Group controlId="formArrival">
          <Form.Label>Arrival</Form.Label>
          <Typeahead
            {...this.state}
            id="basic-example"
            onChange={(selected) => this.setState({ selected })}
            options={nameOfStops}
            placeholder="Choose an arrival stop..."
          />
        </Form.Group>
        <Form.Group controlId="formModeSubmit">
          <Form.Check
            style={{ paddingBottom: "10px" }}
            type="switch"
            id="custom-switch"
            label="Tourist Mode"
          />
        </Form.Group>
      </Form>
    );
  }
}

export default LocationForms;
