import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Typeahead } from "react-bootstrap-typeahead";
import ReactDOM from "react-dom";

import "react-bootstrap-typeahead/css/Typeahead.css";
import "./styles.css";

import * as data from ".././data/db-stops.json";
const uncleanData = data.results;
const myStops = uncleanData.map((stop) => ({
  type: "Feature",
  properties: {
    id: stop.stopid,
    fullname: stop.fullname,
    routes: stop.operators[0].routes,
  },
  geometry: {
    type: "Point",
    pos: {
      lat: parseFloat(stop.latitude),
      lng: parseFloat(stop.longitude),
    },
  },
}));

const cleanStop = uncleanData.map((stop, i) => ({
  id: stop.stopid,
  fullname: stop.fullname,
}));

console.log(cleanStop);

class LocationForms extends Component {
  state = {
    selected: [],
  };

  render() {
    return (
      <Form>
        <Typeahead
          {...this.state}
          id="basic-example"
          onChange={(selected) => this.setState({ selected })}
          options={cleanStop}
          placeholder="Choose a state..."
        />
        <Form.Group controlId="formBasicDeparture">
          <Form.Label>Departure</Form.Label>
          <Form.Control type="input" placeholder="Enter departure stop" />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Arrival</Form.Label>
          <Form.Control type="input" placeholder="Enter arrival stop" />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>

        <Form.Group controlId="exampleForm.ControlSelect1">
          <Form.Label>Example select</Form.Label>
          <Form.Control as="select">
            {cleanStop.map((stop) => (
              <option
                key={stop.id}
                name={stop.fullname}
                value={JSON.stringify(stop)}
              >
                {stop.fullname}
              </option>
            ))}
          </Form.Control>
        </Form.Group>
        <select id="mySelect" onChange={this.onDropdownSelected}>
          {cleanStop.map((stop) => (
            <option
              key={stop.id}
              name={stop.fullname}
              value={JSON.stringify(stop)}
            >
              {stop.fullname}
            </option>
          ))}
        </select>
      </Form>
    );
  }
}

export default LocationForms;
