import React from "react";
import { DirectionsRenderer } from "@react-google-maps/api";

const google = window.google;
class ZipForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      zipcode: "",
    };

    this.inputUpdated = this.inputUpdated.bind(this);
    this.submitZipCode = this.submitZipCode.bind(this);
  }

  submitZipCode(e) {
    e.preventDefault();

    const { zipcode } = this.state;
    const { onSubmit } = this.props;

    onSubmit(zipcode);
    this.setState({ zipcode: "" });
  }

  inputUpdated(e) {
    const { value } = e.target;

    this.setState({ zipcode: value });
  }

  componentDidMount() {
    const directionsService = new google.maps.DirectionsService();

    // we want to make these variable values, to be able to
    // pass this information from what has been selected above.
    const origin = { lat: 53.3472419444, lng: -6.2587911111 };
    const destination = { lat: 53.3466669444, lng: -6.258325 };

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: google.maps.TravelMode.WALKING,
      },
      (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          this.setState({
            directions: result,
          });
          console.log(result);
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );
  }

  render() {
    return (
      <div className="zip-form">
        <form onSubmit={this.submitZipCode}>
          <label htmlForm="zipcode"></label>
          Zip Code
          <input
            className="form-control"
            type="input"
            name="zipcode"
            value={this.state.zipcode}
            onInput={this.inputUpdated}
          />
          <button type="submit" className="btn btn-success">
            Go fuck yourself!
          </button>
        </form>
      </div>
    );
  }
}

export default ZipForm;
