import React, { Component } from "react";
import Form from "react-bootstrap/Form";
import { Typeahead } from "react-bootstrap-typeahead";
import { DirectionsRenderer } from "@react-google-maps/api";

import "react-bootstrap-typeahead/css/Typeahead.css";

import * as data from ".././data/db-stops.json";
const rawData = data.results;

const nameOfStops = rawData.map((stop) => ({
  label: "Stop " + stop.stopid + ", " + stop.fullname,
  id: parseInt(stop.stopid),
}));

class LocationForms extends Component {
  state = {
    selected: [],
  };

  render() {
    return (
      <Form>
        <Form.Group controlId="formDeparture">
          <Form.Label>Departure</Form.Label>
          <Typeahead
            {...this.state}
            id="basic-example"
            onChange={(selected) => this.setState({ selected })}
            options={nameOfStops}
            placeholder="Choose a departure stop..."
          />
        </Form.Group>
        <Form.Group controlId="formArrival">
          <Form.Label>Arrival</Form.Label>
          <Typeahead
            {...this.state}
            id="basic-example"
            onChange={(selected) => this.setState({ selected })}
            options={nameOfStops}
            placeholder="Choose an arrival stop..."
          />
        </Form.Group>
        <Form.Group controlId="formModeSubmit">
          <Form.Check
            style={{ paddingBottom: "10px" }}
            type="switch"
            id="custom-switch"
            label="Tourist Mode"
          />
        </Form.Group>
      </Form>
    );
  }
}

export default LocationForms;

// function to draw directions between points
class Directions extends Component {
  state = {
    directions: null,
  };

  componentDidMount() {
    const directionsService = new google.maps.DirectionsService();

    const origin = { lat: 53.3472419444, lng: -6.2587911111 };
    const destination = { lat: 53.3466669444, lng: -6.258325 };

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          this.setState({
            directions: result,
          });
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );
  }

  render() {
    return <DirectionsRenderer directions={this.state.directions} />;
  }
}
