import React, { useState } from "react";

const axios = require("axios");

export default function RouteStopsApi() {
  const [routeStopsResponse, setRouteStopsResponse] = useState({
    route_stops_response: [],
  });

  React.useEffect(
    () => {
      if (1 === 0) {
        // initial render should be nothing.
        return undefined;
      } else
        axios
          .get(`/api/route_stops`, {
            params: {
              chosenRoute: "46A",
              chosenDirection: "1",
            },
          })
          .then((res) => {
            const modelResponse = res.data;
            setModelResponse(modelResponse);

            console.log(
              "setModelResponse has been triggered with the following values: "
            );
          });
    },
    // Listening for changes to props in order to
    // trigger a call to the API  to re-render the component.
    [props]
  );

  return routeStopsResponse.route_stops_response;
}
