import React, { useState } from "react";
import {
  useLoadScript,
  GoogleMap,
  Marker,
  InfoWindow,
  MarkerClusterer,
} from "@react-google-maps/api";
import useSwr from "swr";

import googleMapApiKey from ".././config";
import * as datal from ".././data/db-stops.json";

// Import custom styles to customize the style of Google Map
const styles = require("../data/GoogleMapStyles.json");

const options = {
  imagePath:
    "https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m",
};

// const fetcher = (...args) =>
//   fetch(...args).then((response) =>
//     response.json().then((data) => {
//       users: data;
//     })
//   );

const fetcher = (...args) => fetch(...args).then((response) => response.json());

export default function T13GoogleMap() {
  // The things we need to track in state
  const [mapRef, setMapRef] = useState(null);
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [markerMap, setMarkerMap] = useState({});
  // consts: [53.349804, -6.260310] - Dublin
  const [center, setCenter] = useState({ lat: 53.349804, lng: -6.26031 });
  const [zoom, setZoom] = useState(11);
  const [infoOpen, setInfoOpen] = useState(false);

  // Load the Google maps scripts
  const { isLoaded } = useLoadScript({
    // Enter your own Google Maps API key in config.js
    googleMapsApiKey: googleMapApiKey,
  });

  const url =
    "https://data.smartdublin.ie/cgi-bin/rtpi/busstopinformation?operator=bac";
  const { data, error } = useSwr(url, { fetcher });
  const stopData = data && !error ? data : { results: 0 };
  console.log("Stop data here", stopData.results);
  const points = stopData.map((stop) => ({
    type: "Feature",
  }));
  console.log(points);

  // const loourl =
  //   "https://data.police.uk/api/crimes-street/all-crime?lat=52.629729&lng=-1.131592&date=2019-10";
  // const { data, errors } = useSwr(loourl, { fetcher });
  // const crimes = data && !errors ? data : [];
  // console.log("Crime data here", crimes);
  // const points = crimes.map((crime) => ({
  //   type: "Feature",
  //   properties: { cluster: false, crimeId: crime.id, category: crime.category },
  //   geometry: {
  //     type: "Point",
  //     coordinates: [
  //       parseFloat(crime.location.longitude),
  //       parseFloat(crime.location.latitude),
  //     ],
  //   },
  // }));
  // console.log(points);

  // console.log(points);

  const uncleanData = datal.results;
  // A GeoJSON Feature object, with the geometry/pos of each object being a GeoJSON Point
  // ref: https://tools.ietf.org/html/rfc7946#section-3.2
  const myStops = uncleanData.map((stop) => ({
    type: "Feature",
    properties: {
      id: stop.stopid,
      fullname: stop.fullname,
      routes: stop.operators[0].routes,
    },
    geometry: {
      type: "Point",
      pos: {
        lat: parseFloat(stop.latitude),
        lng: parseFloat(stop.longitude),
      },
    },
  }));

  // console.log("cunt here again", yourACunt[0]);

  // Iterate myStops to size, center, and zoom map to contain all markers
  const fitBounds = (map) => {
    const bounds = new window.google.maps.LatLngBounds();
    myStops.map((stop) => {
      bounds.extend(stop.geometry.pos);
      return stop.properties.id;
    });
    map.fitBounds(bounds);
  };

  const loadHandler = (map) => {
    // Store a reference to the google map instance in state
    setMapRef(map);
    // Fit map bounds to contain all markers
    fitBounds(map);
  };

  // We have to create a mapping of our places to actual Marker objects
  const markerLoadHandler = (marker, stop) => {
    return setMarkerMap((prevState) => {
      return { ...prevState, [stop.properties.id]: marker };
    });
  };

  const markerClickHandler = (event, place) => {
    // Remember which stop was clicked
    setSelectedPlace(place);

    // Required so clicking a 2nd marker works as expected
    if (infoOpen) {
      setInfoOpen(false);
    }

    setInfoOpen(true);

    // If you want to zoom in a little on marker click
    if (zoom < 11) {
      setZoom(11);
    }

    // if you want to center the selected Marker
    //setCenter(place.pos)
  };

  const renderPage = () => {
    return (
      <GoogleMap
        onLoad={loadHandler}
        onCenterChanged={() => setCenter(mapRef.getCenter().toJSON())}
        center={center}
        zoom={zoom}
        options={{ styles: styles }}
        mapContainerStyle={{
          height: "94vh",
        }}
      >
        <MarkerClusterer options={options}>
          {(clusterer) =>
            myStops.map((stop) => (
              <Marker
                key={stop.properties.id}
                position={stop.geometry.pos}
                clusterer={clusterer}
                onLoad={(marker) => markerLoadHandler(marker, stop)}
                onClick={(event) => markerClickHandler(event, stop)}
              />
            ))
          }
        </MarkerClusterer>

        {infoOpen && selectedPlace && (
          <InfoWindow
            anchor={markerMap[selectedPlace.properties.id]}
            onCloseClick={() => setInfoOpen(false)}
          >
            <div>
              <h3>STOPID: {selectedPlace.properties.id}</h3>
              <h5>ROUTES: {selectedPlace.properties.routes.join(", ")}</h5>
            </div>
          </InfoWindow>
        )}
      </GoogleMap>
    );
  };
  return isLoaded ? renderPage() : null;
}
