import React from "react";
import { Typeahead, Menu, MenuItem } from "react-bootstrap-typeahead";
// Importing the Dublin Bus API stops data
import * as data from "./data/db-small.json";

const { Component } = require("react");
const {
  GoogleMap,
  LoadScript,
  DirectionsRenderer,
  DirectionsService,
} = require("@react-google-maps/api");
// const ScriptLoaded = require("../../docs/ScriptLoaded").default;

// Add in Dublin center
// consts: [53.349804, -6.260310] - Dublin
const dublinCenter = {
  lat: 53.349804,
  lng: -6.30131,
};

// Parsing the Stops data into various object shapes.
const rawData = data.results;
const myStops = rawData.map((stop) => ({
  description: "Stop " + stop.stopid + ", " + stop.fullname,
  type: "Feature",
  properties: {
    id: stop.stopid,
    fullname: stop.fullname,
    routes: stop.operators[0].routes,
  },
  geometry: {
    type: "Point",
    pos: {
      lat: parseFloat(stop.latitude),
      lng: parseFloat(stop.longitude),
    },
  },
}));
const parsedStops = myStops.map((parsed) => ({
  id: parsed.properties.id,
  description: parsed.description,
  geometry: parsed.geometry.pos,
}));
// An array that contains only the names of all the stops.
const stopDescriptions = [];
for (var i = 0; i < parsedStops.length; i++) {
  stopDescriptions.push(parsedStops[i].description);
}
// An array that contains only the names of all the stops.
const stopGeometry = [];
for (var i = 0; i < parsedStops.length; i++) {
  stopGeometry.push(parsedStops[i].geometry);
}

class MapWithDirections extends Component {
  constructor(props) {
    super(props);

    this.state = {
      response: null,
      travelMode: "DRIVING",
      origin: "",
      destination: "",
    };

    this.directionsCallback = this.directionsCallback.bind(this);
    this.checkDriving = this.checkDriving.bind(this);
    this.checkTransit = this.checkTransit.bind(this);
    this.getOrigin = this.getOrigin.bind(this);
    this.getDestination = this.getDestination.bind(this);
    this.onClick = this.onClick.bind(this);
    this.onMapClick = this.onMapClick.bind(this);
  }

  directionsCallback(response) {
    console.log(response);

    if (response !== null) {
      if (response.status === "OK") {
        this.setState(() => ({
          response,
        }));
      } else {
        console.log("response: ", response);
      }
    }
  }

  checkDriving({ target: { checked } }) {
    checked &&
      this.setState(() => ({
        travelMode: "DRIVING",
      }));
  }

  checkTransit({ target: { checked } }) {
    checked &&
      this.setState(() => ({
        travelMode: "TRANSIT",
      }));
  }

  getOrigin(ref) {
    this.origin = ref;
  }

  getDestination(ref) {
    this.destination = ref;
  }

  onClick() {
    if (this.origin.value !== "" && this.destination.value !== "") {
      this.setState(() => ({
        origin: this.origin.value,
        destination: this.destination.value,
      }));
    }
  }

  onMapClick(...args) {
    console.log("onClick args: ", args);
  }

  render() {
    return (
      <div className="map">
        <div className="map-settings">
          <hr className="mt-0 mb-3" />

          <div className="row">
            {/* <div className="col-md-6 col-lg-4">
              <div className="form-group">
                <label htmlFor="ORIGIN-DROPDOWN">Origin - Dropdown</label>
                <br />
                <select ref={this.getOrigin}>
                  {parsedStops.map((stop) => (
                    <option key={stop.id} value={stop.geometry.dut} type="text">
                      {stop.description}
                    </option>
                  ))}
                </select>
              </div>
            </div> */}

            <div className="col-md-6 col-lg-4">
              <Typeahead
                id="basic-example"
                options={stopDescriptions}
                value={stopGeometry.dut}
                type="text"
                ref={this.getOrigin}
                console={console.log()}
                placeholder="Choose a stop..."
              ></Typeahead>
            </div>

            <div className="col-md-6 col-lg-4">
              <div className="form-group">
                <label htmlFor="DESTINATION-DROPDOWN">
                  Destination - Dropdown
                </label>
                <br />
                <select ref={this.getDestination}>
                  {parsedStops.map((stop) => (
                    <option
                      console={console.log(stop.geometry)}
                      key={stop.id}
                      value={stop.geometry.dut}
                      type="text"
                    >
                      {stop.description}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>

          <div className="d-flex flex-wrap">
            <div className="form-group custom-control custom-radio mr-4">
              <input
                id="DRIVING"
                className="custom-control-input"
                name="travelMode"
                type="radio"
                checked={this.state.travelMode === "DRIVING"}
                onChange={this.checkDriving}
              />
              <label className="custom-control-label" htmlFor="DRIVING">
                Driving
              </label>
            </div>

            <div className="form-group custom-control custom-radio mr-4">
              <input
                id="TRANSIT"
                className="custom-control-input"
                name="travelMode"
                type="radio"
                checked={this.state.travelMode === "TRANSIT"}
                onChange={this.checkTransit}
              />
              <label className="custom-control-label" htmlFor="TRANSIT">
                Transit
              </label>
            </div>
          </div>

          <button
            className="btn btn-primary"
            type="button"
            onClick={this.onClick}
          >
            Build Route
          </button>
        </div>

        <div className="map-container">
          <LoadScript googleMapsApiKey="AIzaSyDFfQele6SPurbIljoHv4tVF5USA_7y1-o">
            <GoogleMap
              // required
              id="direction-example"
              // required
              mapContainerStyle={{
                height: "760px",
                width: "100%",
              }}
              // required
              zoom={11}
              // required
              center={dublinCenter}
              // optional
              onClick={this.onMapClick}
              // optional
              onLoad={(map) => {
                console.log("DirectionsRenderer onLoad map: ", map);
              }}
              // optional
              onUnmount={(map) => {
                console.log("DirectionsRenderer onUnmount map: ", map);
              }}
            >
              {this.state.destination !== "" && this.state.origin !== "" && (
                <DirectionsService
                  // required
                  options={{
                    destination: this.state.destination,
                    origin: this.state.origin,
                    travelMode: this.state.travelMode,
                  }}
                  // required
                  callback={this.directionsCallback}
                  // optional
                  onLoad={(directionsService) => {
                    console.log(
                      "DirectionsService onLoad directionsService: ",
                      directionsService
                    );
                  }}
                  // optional
                  onUnmount={(directionsService) => {
                    console.log(
                      "DirectionsService onUnmount directionsService: ",
                      directionsService
                    );
                  }}
                />
              )}

              {this.state.response !== null && (
                <DirectionsRenderer
                  // required
                  options={{
                    directions: this.state.response,
                  }}
                  // optional
                  onLoad={(directionsRenderer) => {
                    console.log(
                      "DirectionsRenderer onLoad directionsRenderer: ",
                      directionsRenderer
                    );
                  }}
                  // optional
                  onUnmount={(directionsRenderer) => {
                    console.log(
                      "DirectionsRenderer onUnmount directionsRenderer: ",
                      directionsRenderer
                    );
                  }}
                />
              )}
            </GoogleMap>
          </LoadScript>
        </div>
      </div>
    );
  }
}

export default MapWithDirections;
