import React from "react";

const axios = require("axios");

const ModelApi = (props) => {
  const [state, setState] = React.useState({ fullname: "" });
  const [modelResponse, setModelResponse] = React.useState([]);
  const onClick = () => setModelResponse(modelResponse);

  const stateChange = (f) => {
    const { name, value } = f.target;
    setState({
      [name]: value,
    });
  };

  React.useEffect(() => {
    axios
      .get(`/api/model_result`, {
        params: {
          chosenTime: parseInt(props.timeDayMonth[0]),
          chosenDay: props.timeDayMonth[1],
          chosenMonth: props.timeDayMonth[2],
        },
      })
      .then((res) => {
        const modelResponse = res.data;
        setModelResponse(modelResponse);
      });
  }, []);
  return (
    <div>
      <label htmlFor="fullname"> Full Name: </label>
      <input type="text" name="fullname" onChange={stateChange} />
      <p>{modelResponse.model_response}</p>
      <div className="border border-primary py-3">
        <h4> Greetings, {state.fullname}!</h4>
      </div>
    </div>
  );
};

export default ModelApi;
