/*global google*/
import React, { Component } from "react";
import { DirectionsRenderer } from "@react-google-maps/api";
import Form from "react-bootstrap/Form";
import { Typeahead } from "react-bootstrap-typeahead";
import usePlacesAutocomplete, {
  getGeocode,
  getLatLng,
} from "use-places-autocomplete";
import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxList,
  ComboboxOption,
} from "@reach/combobox";

import * as data from ".././data/db-stops.json";

const rawData = data.results;

class Directions extends Component {
  state = {
    directions: null,
    selected: [],
  };

  render() {
    return (
      <div>
        <Search />
        <DirectionsRenderer directions={this.state.directions} />
      </div>
    );
  }

  componentDidMount() {
    const directionsService = new google.maps.DirectionsService();

    console.log(console.log(this.state.selected));

    // we want to make these variable values, to be able to
    // pass this information from what has been selected above.
    const origin = { lat: 53.3472419444, lng: -6.2587911111 };
    const destination = { lat: 53.3466669444, lng: -6.258325 };

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        travelMode: google.maps.TravelMode.WALKING,
      },
      (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          this.setState({
            directions: result,
          });
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );
  }
}

export default Directions;

function Search() {
  const {
    ready,
    value,
    suggestions: { status, data },
    setValue,
    clearSuggestions,
  } = usePlacesAutocomplete({
    requestOptions: {
      location: { lat: () => 53.349804, lng: () => -6.26031 },
      radius: 100 * 1000,
    },
  });

  return (
    <div className="search">
      <Combobox
        onSelect={(address) => {
          console.log(address);
        }}
      >
        <ComboboxInput
          value={value}
          onChange={(e) => {
            setValue(e.target.value);
          }}
          disabled={!ready}
          placeholder="Enter an address"
        />
        <ComboboxPopover>
          <ComboboxList>
            {status === "OK" &&
              data.map(({ id, description }) => (
                <ComboboxOption key={id} value={description} />
              ))}
          </ComboboxList>
        </ComboboxPopover>
      </Combobox>
    </div>
  );
}

// function that uses the mystops data in the same
// way that the Search function above uses the usePlacesAutocomplete
