import React from "react";
import usePlacesAutocomplete, {
  getGeocode,
  getLatLng,
} from "use-places-autocomplete";
const { Component } = require("react");
const {
  DirectionsRenderer,
  DirectionsService,
  Autocomplete,
} = require("@react-google-maps/api");
// // consts: [53.349804, -6.260310] - Dublin
// const bounds = {
//   lat: 53.349804,
//   lng: -6.26031,
// };

// const shit = new window.google.map.LatLng(53.349804, -6.26031);

class Directions extends Component {
  constructor(props) {
    super(props);

    this.state = {
      response: null,
      travelMode: "TRANSIT",
      origin: "",
      destination: "",
    };

    this.directionsCallback = this.directionsCallback.bind(this);
    this.getOrigin = this.getOrigin.bind(this);
    this.getDestination = this.getDestination.bind(this);
    this.onClick = this.onClick.bind(this);
    this.onMapClick = this.onMapClick.bind(this);

    this.autocomplete = null;

    this.onLoad = this.onLoad.bind(this);
    this.onPlaceChanged = this.onPlaceChanged.bind(this);
  }

  directionsCallback(response) {
    console.log("hi, ", response);

    if (response !== null) {
      if (response.status === "OK") {
        this.setState(() => ({
          response,
        }));
      } else {
        console.log("response: ", response);
      }
    }
  }

  getOrigin(ref) {
    this.origin = ref;
  }

  getDestination(ref) {
    this.destination = ref;
  }

  onClick() {
    if (this.origin.value !== "" && this.destination.value !== "") {
      this.setState(() => ({
        origin: this.origin.value,
        destination: this.destination.value,
      }));
    }
  }

  onMapClick(...args) {
    console.log("onClick args: ", args);
  }

  onLoad(autocomplete) {
    console.log("autocomplete: ", autocomplete);

    this.autocomplete = autocomplete;
  }

  onPlaceChanged() {
    if (this.autocomplete !== null) {
      console.log(this.autocomplete.getPlace());
    } else {
      console.log("Autocomplete is not loaded yet!");
    }
  }

  myLatLng = new window.google.maps.LatLng({ lat: 53.349804, lng: -6.26031 });

  render() {
    return (
      <div className="">
        <div className="boxes">
          <div className="row">
            <div className="col-md-6 col-lg-4">
              <div className="">
                <input
                  id="ORIGIN"
                  className="form-control"
                  type="text"
                  placeholder="Origin"
                  ref={this.getOrigin}
                />
              </div>
            </div>

            <div className="col-md-6 col-lg-4">
              <div className="">
                <Autocomplete
                  onLoad={this.onLoad}
                  onPlaceChanged={this.onPlaceChanged}
                  restrictions={window.google.maps.LatLngBounds({
                    lat: 53.349804,
                    lng: -6.26031,
                  })}
                  // bounds={{
                  //   north: 51.09,
                  //   south: 54.29,
                  //   west: -7.02,
                  //   east: -5.26,
                  // }}
                >
                  <input
                    type="text"
                    placeholder="Customized your placeholder"
                    style={{
                      boxSizing: `border-box`,
                      border: `1px solid transparent`,
                      width: `240px`,
                      height: `32px`,
                      padding: `0 12px`,
                      borderRadius: `3px`,
                      boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
                      fontSize: `14px`,
                      outline: `none`,
                      textOverflow: `ellipses`,
                      position: "absolute",
                      left: "50%",
                      marginLeft: "-120px",
                    }}
                  />
                </Autocomplete>
              </div>
            </div>

            <div className="col-md-6 col-lg-4">
              <div className="">
                <input
                  id="DESTINATION"
                  className="form-control"
                  type="text"
                  placeholder="Destination"
                  ref={this.getDestination}
                />
              </div>
            </div>
            <div className="col-md-6 col-lg-4">
              <div className="">
                <button
                  className="btn btn-primary"
                  type="button"
                  onClick={this.onClick}
                >
                  Build Route
                </button>
              </div>
            </div>
          </div>
        </div>

        <div className="map-container">
          {this.state.destination !== "" && this.state.origin !== "" && (
            <DirectionsService
              // required
              options={{
                destination: this.state.destination,
                origin: this.state.origin,
                travelMode: this.state.travelMode,
              }}
              // required
              callback={this.directionsCallback}
            />
          )}

          {this.state.response !== null && (
            <DirectionsRenderer
              // required
              options={{
                directions: this.state.response,
              }}
              panel={document.getElementById("panel")}
            />
          )}
        </div>
      </div>
    );
  }
}

export default Directions;

function PlaceSearch() {
  const {
    ready,
    value,
    suggestions: { status, data },
    setValue,
    clearSuggestions,
  } = usePlacesAutocomplete({
    requestOptions: {
      location: { lat: () => 53.349804, lng: () => -6.26031 },
      radius: 100 * 1000,
    },
  });

  // https://developers.google.com/maps/documentation/javascript/reference/places-autocomplete-service#AutocompletionRequest

  const handleInput = (e) => {
    setValue(e.target.value);
  };

  const handleSelect = async (address) => {
    setValue(address, false);
    clearSuggestions();

    try {
      const results = await getGeocode({ address });
      const { lat, lng } = await getLatLng(results[0]);
      panTo({ lat, lng });
    } catch (error) {
      console.log("ðŸ˜± Error: ", error);
    }
  };

  return (
    <div className="search">
      <Combobox onSelect={handleSelect}>
        <ComboboxInput
          value={value}
          onChange={handleInput}
          disabled={!ready}
          placeholder="Search your location"
        />
        <ComboboxPopover>
          <ComboboxList>
            {status === "OK" &&
              data.map(({ id, description }) => (
                <ComboboxOption key={id} value={description} />
              ))}
          </ComboboxList>
        </ComboboxPopover>
      </Combobox>
    </div>
  );
}
