import React, { useState } from "react";
const axios = require("axios");

function useForceUpdate() {
  let [value, setState] = useState(true);
  return () => setState(!value);
}

let count = 0;

export default function BlApp(props) {
  const [modelResponse, setModelResponse] = React.useState([]);
  const [hour, setHour] = React.useState();
  const [day, setDay] = React.useState();
  const [month, setMonth] = React.useState();

  const hourChoice = () => {
    setHour(() => props.timeDayMonth[0]);
  };

  const dayChoice = () => {
    setDay(() => props.timeDayMonth[1]);
  };

  const monthChoice = () => {
    setMonth(() => props.timeDayMonth[2]);
  };

  const [query, setQuery] = useState(["9", "Thu", "May"]);
  console.log(
    "These are the time values when received in BlApp",
    props.timeDayMonth[0],
    props.timeDayMonth[1],
    props.timeDayMonth[2]
  );

  React.useEffect(() => {
    axios
      .get(`/api/model_result`, {
        params: {
          chosenTime: hour,
          chosenDay: day,
          chosenMonth: month,
        },
      })
      .then((res) => {
        const modelResponse = res.data;
        setModelResponse(modelResponse);
        console.log("setModelResponse has been triggered");
      });
  }, [query]);
  let forceUpdate = useForceUpdate();

  console.log(modelResponse);
  return (
    <div>
      <h1>{count++} times clicked</h1>
      <p>
        This journey is estimated to take {modelResponse.model_response} minutes
      </p>
      <button onClick={() => setModelResponse(modelResponse)}>Refresh</button>
    </div>
  );
}
